const READ_ItemS = '/Items'
const DELETE_Item = '/Items/:ItemId';
const CREATE_Item = '/Items/new'
const READ_Item = '/Items/spotsid'

export const createItem = (items) => async (dispatch) => {
    const {item, stars, spotId} = items
 const data = await fetch(`/api/item` , {
        method: "POST",
        body: JSON.stringify({
           item, stars
        }),
    });
    const response = await data.json()

    dispatch(createItems(response))
    return response
}

export const createItems = (Item) =>({
    type: CREATE_Item,
    payload: Item
})
export const getItem = (itemId) => async (dispatch) => {
const data = await fetch(`/api/spots/${itemId}/Items`)
    const response = await data.json()
    dispatch(getItems(response))
    return response
}
export const getItems = (Item) =>({
    type: READ_ItemS,
    payload: Item
})
export const deleteItems = (id) => async (dispatch) => {
    const response = await fetch(`/api/Items/${id}`, {
        method: 'DELETE',
    });
    dispatch(deleteItem(id));
    return response;
};
export  const deleteItem = (ItemId) => {
    return {
        type: DELETE_Item,
        ItemId
    };
};

const initialState =  {allItems:{}, singleSpotItems: {}}
export const ItemsReducer = (state = initialState, action) => {
    let newState = {}

    switch (action.type) {
               case CREATE_Item:
                 newState = { allItems:{...state.allItems}} 
                newState.allItems[action.payload.id] = action.payload
                return newState

                case READ_ItemS:
                 newState ={allItems: {} };
                 action.payload.Items.forEach(Items => newState.allItems[Items.id] = Items)
                return newState

                case READ_Item:
                 newState ={singleSpotItems: {} };
                 action.payload.Items.forEach(Items => newState.singleSpotItems[Items.id] = Items)
                return newState 

                case DELETE_Item:
             newState = {...state}
             delete newState[action.payload]

      default:
    return state
}}
export default ItemsReducer
